#!/usr/bin/env bash\nset -euo pipefail\n\necho "[entrypoint] updating pip and installing requirements if present..."\npython -m pip install --upgrade pip setuptools wheel\nif [ -f /workspace/requirements-extensions.txt ]; then\n  pip install -r /workspace/requirements-extensions.txt\nfi\nif [ -f /workspace/requirements.txt ]; then\n  pip install -r /workspace/requirements.txt\nfi\n\necho "[entrypoint] starting command: $@"\nexec "$@"\n